INSERT INTO hotels (id, owner_id, name, description, city, price_per_night, image_url, amenities, rating, total_rooms, available_rooms) VALUES
  ('hotel-1', 'owner_1', 'Sunset Paradise Resort', 'Luxury beachfront resort with stunning ocean views, infinity pool, and world-class spa facilities', 'Mumbai', 150, '/placeholder.svg?height=300&width=500', ARRAY['Free WiFi', 'Swimming Pool', 'Spa', 'Beach Access', 'Restaurant', 'Bar'], 4.8, 50, 45),
  ('hotel-2', 'owner_2', 'City Center Business Hotel', 'Modern business hotel in the heart of the city with conference facilities and executive lounge', 'Delhi', 120, '/placeholder.svg?height=300&width=500', ARRAY['Free WiFi', 'Business Center', 'Gym', 'Restaurant', 'Conference Rooms'], 4.5, 80, 72),
  ('hotel-3', 'owner_3', 'Mountain View Lodge', 'Cozy mountain lodge with panoramic views, perfect for nature lovers and adventure seekers', 'Manali', 80, '/placeholder.svg?height=300&width=500', ARRAY['Mountain Views', 'Hiking Trails', 'Fireplace', 'Restaurant', 'Free Parking'], 4.6, 25, 20),
  ('hotel-4', 'owner_4', 'Heritage Palace Hotel', 'Restored heritage property offering royal experience with traditional architecture and modern amenities', 'Jaipur', 200, '/placeholder.svg?height=300&width=500', ARRAY['Heritage Property', 'Royal Suites', 'Traditional Dining', 'Cultural Shows', 'Spa'], 4.9, 30, 25),
  ('hotel-5', 'owner_5', 'Backpacker Hostel', 'Budget-friendly hostel with clean dormitories and private rooms, perfect for solo travelers', 'Goa', 25, '/placeholder.svg?height=300&width=500', ARRAY['Shared Kitchen', 'Common Area', 'Free WiFi', 'Laundry', 'Bike Rental'], 4.2, 40, 35);
INSERT INTO user_posts (id, user_id, user_name, user_avatar, title, content, city, latitude, longitude, image_url, likes, comments) VALUES
  ('post-1', 'user_123', 'Adventure Seeker', 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=150&h=150&fit=crop&crop=face', 'Hidden Gems in Mumbai', 'Just discovered this amazing street food market in Bandra that''s not in any guidebook! The vada pav here is incredible and costs only â‚¹15. Perfect spot for authentic Mumbai experience.', 'Mumbai', 19.0760, 72.8777, '/placeholder.svg?height=400&width=600', 24, 5),
  ('post-2', 'user_456', 'Foodie Explorer', 'https://images.unsplash.com/photo-1494790108755-2616b612b786?w=150&h=150&fit=crop&crop=face', 'Best Butter Chicken in Delhi', 'Found this incredible family-run restaurant in Old Delhi serving the most amazing butter chicken I''ve ever had. The owner shared stories about recipes passed down for 4 generations!', 'Delhi', 28.6139, 77.2090, '/placeholder.svg?height=400&width=600', 31, 8),
  ('post-3', 'user_789', 'Mountain Lover', 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face', 'Sunrise Trek in Manali', 'Completed the most beautiful sunrise trek to Jogini Falls today! Started at 4 AM and the view was absolutely worth it. Pro tip: carry warm clothes even in summer.', 'Manali', 32.2396, 77.1887, '/placeholder.svg?height=400&width=600', 18, 3),
  ('post-4', 'user_101', 'Culture Enthusiast', 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=150&h=150&fit=crop&crop=face', 'Royal Experience in Jaipur', 'Stayed at this amazing heritage hotel and felt like royalty! The architecture is stunning and they organize cultural shows every evening. Don''t miss the traditional Rajasthani thali.', 'Jaipur', 26.9124, 75.7873, '/placeholder.svg?height=400&width=600', 42, 12),
  ('post-5', 'user_202', 'Beach Bum', 'https://images.unsplash.com/photo-1500648767791-00dcc994a43e?w=150&h=150&fit=crop&crop=face', 'Secret Beach in Goa', 'Discovered this hidden beach in South Goa with crystal clear water and almost no tourists. Perfect for a peaceful day away from the crowds. Best accessed by scooter.', 'Goa', 15.2993, 74.1240, '/placeholder.svg?height=400&width=600', 35, 7);
INSERT INTO chat_messages (id, user_id, user_name, user_avatar, message, city, latitude, longitude) VALUES
  ('msg-1', 'user_123', 'Adventure Seeker', 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=150&h=150&fit=crop&crop=face', 'Hey everyone! Just arrived in Mumbai. Any recommendations for authentic street food?', 'Mumbai', 19.0760, 72.8777, NOW() - INTERVAL '2 hours'),
  ('msg-2', 'user_456', 'Local Guide', 'https://images.unsplash.com/photo-1494790108755-2616b612b786?w=150&h=150&fit=crop&crop=face', 'Welcome to Mumbai! You must try the vada pav at Ashok Vada Pav near Kirti College. It''s legendary!', 'Mumbai', 19.0760, 72.8777, NOW() - INTERVAL '1 hour 45 minutes'),
  ('msg-3', 'user_789', 'Foodie Explorer', 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face', 'Also check out Mohammed Ali Road for amazing kebabs and biryanis. Best time to go is after sunset!', 'Mumbai', 19.0760, 72.8777, NOW() - INTERVAL '1 hour 30 minutes'),
  ('msg-4', 'user_101', 'Travel Buddy', 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=150&h=150&fit=crop&crop=face', 'Anyone planning to visit Red Fort tomorrow? Looking for company to explore together!', 'Delhi', 28.6139, 77.2090, NOW() - INTERVAL '3 hours'),
  ('msg-5', 'user_202', 'History Buff', 'https://images.unsplash.com/photo-1500648767791-00dcc994a43e?w=150&h=150&fit=crop&crop=face', 'I''m in! Red Fort is amazing. We should also visit Jama Masjid nearby. The view from the minaret is incredible.', 'Delhi', 28.6139, 77.2090, NOW() - INTERVAL '2 hours 30 minutes');
